export default {
  data: function data() {
    return {
      codes: [],
      ctx: "",
      colors: ["red", "yellow", "blue", "green", "pink", "black"],
      code_Len: 4
    };
  },
  mounted: function mounted() {
    this.draw();
  },
  computed: {
    codeString: function codeString() {
      var result = "";

      for (var i = 0; i < this.codes.length; i++) {
        result += this.codes[i];
      }

      return result.toUpperCase();
    }
  },
  watch: {
    codeString: function codeString(newValue) {
      this.$emit("change", newValue);
    }
  },
  methods: {
    generateRandom: function generateRandom() {
      this.codes = [];
      var chars = "abcdefghijkmnpqrstuvwxyzABCDEFGHJKLMNPQRSTUVWXYZ23456789";
      var charsArr = chars.split("");

      for (var i = 0; i < this.code_Len; i++) {
        var num = Math.floor(Math.random() * charsArr.length);
        this.codes.push(charsArr[num]);
      }
    },
    draw: function draw() {
      this.generateRandom();
      this.drawText();
    },
    drawLine: function drawLine() {
      var lineNumber = 3; // 线条条数

      var lineX = 140;
      var lineY = 30; // 最大线条坐标

      for (var i = 0; i < lineNumber; i++) {
        this.ctx.strokeStyle = this.colors[Math.floor(Math.random() * 5)];
        this.ctx.beginPath();
        this.ctx.moveTo(Math.floor(Math.random() * lineX), Math.floor(Math.random() * lineY));
        this.ctx.lineTo(Math.floor(Math.random() * lineX), Math.floor(Math.random() * lineY));
        this.ctx.stroke();
      }
    },
    drawText: function drawText() {
      var canvas = this.$refs["canvas"];
      this.ctx = canvas.getContext("2d");
      this.ctx.fillStyle = "#BFEFFF";
      this.ctx.fillRect(0, 0, 140, 40);
      this.ctx.font = "20px Verdana";
      var x = 15;

      for (var i = 0; i < this.code_Len; i++) {
        this.ctx.fillStyle = this.colors[Math.floor(Math.random() * 5)];
        this.ctx.fillText(this.codes[i], x, 25);
        x = x + 15;
      }

      this.drawLine();
    }
  }
};
