export default {
  methods: {
    bodyScroll: function bodyScroll() {
      var scrollTop = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop;
      document.cookie = "scroll=" + scrollTop;
    },
    scrollback: function scrollback() {
      var scroll = this.GetCookie('scroll');

      if (scroll != null) {
        document.body.scrollTop = scroll;
      }
    },
    GetCookie: function GetCookie(sName) {
      var aCookie = document.cookie.split(";"); //console.log(aCookie);

      for (var i = 0; i < aCookie.length; i++) {
        var aCrumb = aCookie[i].split("=");

        if (sName == aCrumb[0]) {
          return aCrumb[1];
        }
      }

      return null;
    }
  },
  mounted: function mounted() {
    var _this = this;

    // this.$i18n.locale='en'
    this.$nextTick(function () {
      window.addEventListener("scroll", _this.bodyScroll, true);
      window.addEventListener("load", _this.scrollback, true);
    });
  }
};
